from datetime import datetime


class Solution(object):
    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        initial_len = len(s)
        largest = 0
        mapper = []
        for i in range(initial_len):
            if (initial_len - i) > largest:
                for j in range(i + 1, initial_len + 1):
                    ns = s[i:j]
                    new_s_len = len(ns)
                    if new_s_len > largest and new_s_len not in mapper:
                        st = []
                        is_valid = True
                        if new_s_len not in mapper:
                            for c in ns:
                                if c == "(":
                                    st.append(")")
                                elif not st or c != st.pop():
                                    is_valid = False
                                    break
                            is_valid = is_valid and not st
                            if is_valid and new_s_len > largest:
                                largest = new_s_len
                                mapper.append(new_s_len)
        return largest
    


